// ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå
const result = [];

for (const page of Object.values(grouped)) {
  let content = page.content;

  // ‡∏™‡∏£‡πâ‡∏≤‡∏á RegEx ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏´‡∏≤ ![xxx](img-xx.jpeg)
  const regex = /!\[([^\]]*?)\]\((img-\d+\.jpeg)\)/g;

  // ‡πÅ‡∏ó‡∏ô‡∏ó‡∏µ‡πà‡∏ó‡∏∏‡∏Å‡∏†‡∏≤‡∏û‡πÉ‡∏ô content ‡∏ñ‡πâ‡∏≤‡πÄ‡∏à‡∏≠ link ‡∏ó‡∏µ‡πà‡∏ï‡∏£‡∏á‡πÉ‡∏ô image_map
  content = content.replace(regex, (match, altText, fileName) => {
    const url = page.image_map[fileName];
