parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "Intent Type": {
      "type": "string",
      "enum": ["Product Inquiry", "Issue Report"],
      "description": "Intent Type"
    },
    "Message History": {
      "type": "string",
      "description": "The full conversation with the customer, using the same language the customer used"
    },
    "Reported Issue": {
      "type": "string",
      "description": "The problem the customer reported, plus a clearly summarized explanation in Thai",
      "default": ""
    },
    "Image Attached Status": {
      "type": "string",
      "enum": ["Y", "N"],
      "description": "Indicates whether an image was attached",
      "default": "N"
    },
    "Primary Language Used": {
      "type": "string",
      "description": "Language used in communication with the customer, e.g., Thai, English, etc.",
      "default": ""
    },
    "Image Link": {
      "type": "string",
      "description": "The link to the attached image (or empty if none)",
      "default": ""
    },
    "Customer Name": {
      "type": "string",
      "description": "Name of the customer (or empty if none)",
      "default": ""
    },
    "Location": {
      "type": "string",
      "description": "Location related to the issue (or empty if none)",
      "default": ""
    },
    "Contact Number": {
      "type": "string",
      "description": "Phone number of the customer (or empty if none)",
      "default": ""
    },
    "Email": {
      "type": "string",
      "description": "Email address of the customer (or empty if none)",
      "default": ""
    },
    "Customer Confirmation Status": {
      "type": "string",
      "enum": ["Y", "N"],
      "description": "Whether the customer has confirmed the correctness of the information",
      "default": "N"
    }
  },
  "required": [
    "Message History",
    "Reported Issue",
    "Image Attached Status",
    "Primary Language Used",
    "Image Link",
    "Customer Name",
    "Location",
    "Contact Number",
    "Email",
    "Customer Confirmation Status"
  ]
}
