const requestedAmount = parseInt($('Search Lead Form').first().json['รายการที่ต้องการดึงข้อมูล'], 10) || 10; // ดึงค่าจากฟอร์ม (default 10)
const resultsPerPage = 10;

const output = [];

for (let i = 1; i <= requestedAmount; i += resultsPerPage) {
  output.push({ json: { start: i } });
}

return output;

return items.map(item => {
  const target = item.json["กลุ่มลูกค้าเป้าหมายที่ต้องการ"] || "";
  const area = item.json["เขตพื้นที่ที่ต้องการ (ถ้ามี)"] || "";

  // รวมข้อความทั้งสองช่อง
  const combined = $('Search Lead Form').first().json['กลุ่มลูกค้าเป้าหมายที่ต้องการ'] + " " + $('Search Lead Form').first().json['เขตพื้นที่ที่ต้องการ (ถ้ามี)'];

  // แยกคำด้วย , หรือ ช่องว่างหลายตัว และลบช่องว่างส่วนเกิน
  const keywords = combined
    .split(/[,\s]+/)
    .map(word => word.trim())
    .filter(word => word !== "");

  // รวมเป็นสตริงเดียวด้วยเครื่องหมาย +
  const searchQuery = keywords.join("+");

  return {
    json: {
      ...item.json,
      searchQuery
    }
  };
});

const fullUrl = $json.search_item.link;

// ใช้ RegExp ดึง domain + ชื่อเพจ (เช่น https://www.facebook.com/FarmhouseThai)
const match = fullUrl.match(/^https:\/\/www\.facebook\.com\/[^\/]+/);

return {
  json: {
    title: $json.search_item.title,
    detail: $json.search_item.snippet,
    baseUrl: match ? match[0] : null,
    originalUrl: fullUrl        
  }
};

